SHORTCUT "AirPlay to the TV"
ACTION comment WFCommentActionText="Wakes the Apple TV and sets the playback destination to that device, making any videos play over AirPlay."
ACTION com.apple.TVRemoteUIService.WakeAppleTVIntent device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
ACTION setplaybackdestination WFMediaRoute={"routeName": "Living Room TV", "groupUID": "2E30F300-272F-4188-8C58-638E6DDE4EBC", "routeUID": "6C18F272-DF3B-457F-A4C6-AE2FADC55556", "isLocalDevice": false}

SHORTCUT "Benny Hill"
ACTION comment WFCommentActionText="Plays the track from The Benny Hilly Show in Apple Music."
ACTION playmusic WFPlayMusicActionShuffle="Off" WFMediaItems={"type": "PlaybackArchive", "playbackArchiveData": "", "itemName": "The Benny Hill Show"}

SHORTCUT "Get network details"
ACTION comment WFCommentActionText="Gets the current Wi-Fi network details, including the current IP address, Network Name, BSSSID, Wi-Fi Standard, RX Rate, TX Rate, RSSI, Noise, Channel Number, and Hardware MAC Address."
ACTION getwifi
ACTION getwifi WFWiFiDetail="BSSID"
ACTION getwifi WFWiFiDetail="Wi-Fi Standard"
ACTION getwifi WFWiFiDetail="RX Rate"
ACTION getwifi WFWiFiDetail="TX Rate"
ACTION getwifi WFWiFiDetail="RSSI"
ACTION getwifi WFWiFiDetail="Noise"
ACTION getwifi WFWiFiDetail="Channel Number"
ACTION getwifi WFWiFiDetail="Hardware MAC Address"
ACTION getipaddress WFIPAddressSourceOption="Local" WFIPAddressTypeOption="IPv4"
ACTION gettext WFTextActionText=`Current IP address: {@prev}

Network Name: {@getwifi}
BSSID: {@getwifi}
Wi-Fi Standard: {@getwifi}
RX Rate: {@getwifi}
TX Rate: {@getwifi}
RSSI: {@getwifi}
Noise: {@getwifi}
Channel Number: {@getwifi}
Hardware MAC Address: {@getwifi}`
ACTION showresult Text=`{@prev}`
ACTION output WFNoOutputSurfaceBehavior="Copy to Clipboard" WFOutput=`{@prev}` WFResponse=`{@prev}`

SHORTCUT "I’m working in the living room"
ACTION comment WFCommentActionText="Turns on the living room lights, plus casts my music to the Living Room TV."
ACTION homeaccessory WFHomeTriggerActionSets={"WFHFTriggerActionSetsBuilderParameterStateActionSets": [{"HMActionSetSerializedDictionaryProtocol": "ProtoBuf", "HMActionSetSerializedDictionaryVersion": "1.0", "HMActionSetSerializedData": "<binary:3254bytes>"}], "WFHFTriggerActionSetsBuilderParameterStateHome": "A21E2F4C-F149-4C30-B1CA-6935E385D8D0"}
ACTION setplaybackdestination WFMediaRouteOperation="Set" WFMediaRoute={"routeName": "Living Room TV", "groupUID": "2B9998C3-E275-5206-B49C-B14DE90B3985", "routeUID": "2B9998C3-E275-5206-B49C-B14DE90B3985", "isLocalDevice": false}

SHORTCUT "Open my Work notes"
ACTION comment WFCommentActionText="Opens the “Work” folder in Notes – when in Gallery view, this will also show your sub-folders."
ACTION com.apple.mobilenotes.ICNotesFolderIntent folder={"displayString": "Work", "displayImage": {"type": "Data", "imageSize": {"width": 29.5, "height": 23.5}, "imageData": ""}, "identifier": "applenotes://showFolder?objectIDURI=x-coredata://36EC2EFD-3938-49EE-98EE-FF9B3871CF45/ICFolder/p3098"}

SHORTCUT "Play Apple Music Hits"
ACTION comment WFCommentActionText="Opens the Apple Music Hits radio station that plays top tracks from the service (as opposed to the main Apple Music 1 channel)."
ACTION playmusic WFMediaItems={"type": "PlaybackArchive", "playbackArchiveData": "", "itemName": "Apple Music Hits"}

SHORTCUT "Show my Sleep data"
ACTION comment WFCommentActionText="Opens the Health app into the Sleep category so you can check data tracked by Apple Watch or other apps."
ACTION com.apple.Health.OpenViewIntent target={"title": {"key": "Sleep"}, "symbol": {"systemName": "bed.double.fill"}, "identifier": "HKDisplayCategoryIdentifier_5", "subtitle": {"key": "Sleep"}}

SHORTCUT "Show News+ Audio Stories"
ACTION comment WFCommentActionText="Opens into the Audio Stories section of Apple News+ so you can listen on your device."
ACTION useractivity.open AppBundleIdentifier="com.apple.news" UserActivityData="<binary:1411bytes>" UserActivityDescriptor={"TeamIdentifier": "ZL6BUSYGB3", "BundleIdentifier": "com.apple.news", "Name": "News", "UserActivityType": "com.apple.news.feedBrowsing"}

SHORTCUT "Switch to my profile"
ACTION comment WFCommentActionText="Turns on the TV and sets the Apple TV to your personal profile, appearance preferance, and Reduce Loud Sounds setting, then shows the remote."
MENU "Which TV?"
CASE "Living Room"
ACTION com.apple.TVRemoteUIService.WakeAppleTVIntent device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
ACTION com.apple.TVRemoteUIService.SwitchUserAccountIntent account={"displayString": "Matthew Cassinelli", "pronunciationHint": "Matthew Cassinelli", "identifier": "44A2F8B2-491E-46AC-BA40-96E371E585B1"} device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
ACTION com.apple.TVRemoteUIService.ReduceLoudSoundsIntent toggle=0 device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
ACTION com.apple.TVRemoteUIService.ToggleSystemAppearanceIntent appearanceToggle="dark" device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
ACTION com.apple.TVRemoteUIService.LaunchRemoteIntent device={"displayString": "Living Room TV", "pronunciationHint": "Living Room TV", "identifier": "af50d3fa05e6"}
CASE "Family Room"
ACTION com.apple.TVRemoteUIService.WakeAppleTVIntent device={"displayString": "Family Room TV", "pronunciationHint": "Family Room TV", "identifier": "ebbd24874a17"}
ACTION com.apple.TVRemoteUIService.SwitchUserAccountIntent account={"displayString": "Matthew Cassinelli", "pronunciationHint": "Matthew Cassinelli", "identifier": "44A2F8B2-491E-46AC-BA40-96E371E585B1"} device={"displayString": "Family Room TV", "pronunciationHint": "Family Room TV", "identifier": "ebbd24874a17"}
ACTION com.apple.TVRemoteUIService.ReduceLoudSoundsIntent toggle=0 device={"displayString": "Family Room TV", "pronunciationHint": "Family Room TV", "identifier": "ebbd24874a17"}
ACTION com.apple.TVRemoteUIService.ToggleSystemAppearanceIntent appearanceToggle="dark" device={"displayString": "Family Room TV", "pronunciationHint": "Family Room TV", "identifier": "ebbd24874a17"}
ACTION com.apple.TVRemoteUIService.LaunchRemoteIntent device={"displayString": "Family Room TV", "pronunciationHint": "Family Room TV", "identifier": "ebbd24874a17"}
ENDMENU
